sp_widget.script:
(function() {
  if (!input) {
    data.domains = [];
    var grDomain = new GlideRecordSecure('x_1383177_lms_app_course_domain');
    grDomain.addActiveQuery();
    grDomain.query();

    while (grDomain.next()) {
      data.domains.push({
        Name: grDomain.getDisplayValue('name'),
        sys_id: grDomain.getUniqueValue()
      });
    }
  }
})();
