sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	/*step 1 Load initial data from server*/

	if (!input) {
		data.domains = [];
		var grdomain = new GlideRecordSecure('x_1383177_lms_app_course_domain');
		grdomain.addActiveQuery();
		grdomain.query();

		while (grdomain.next()) {
			data.domains.push({
				Name : grdomain.getDisplayValue('name'),
				Description : grdomain.getDisplayValue('description'),
				Thumbnail : grdomain.getDisplayValue('thumbnail'),
				'sys_id' : grdomain.getUniqueValue()
			})			
		}
	}
	/*step4 process user input*/
	if (input) {

	}
})();
