sp_widget.client_script:
api.controller = function($scope) {
  var c = this;

  c.filterCoursesByDomain = function(domainName) {
    console.log('Filtering courses by domain:', domainName);
    $scope.$emit('filterCoursesByDomain', domainName);
  };
};

