sp_widget.client_script:
api.controller = function($scope) {
    var c = this;
    c.latestEnrollment = {};

    // Function to fetch latest enrolled course and progress
    c.fetchLatestEnrolledCourse = function() {
        // Check if there is a latest enrollment
			console.log("Init fetch");
        if (c.data.latestEnrollment && c.data.latestEnrollment.course) {
						console.log("Latest Course Recieved!");
            c.latestEnrollment = c.data.latestEnrollment.course;
            c.progress = 50; // Example progress percentage, replace with actual logic
        } else {
            // No latest enrollment found
					console.log("No Course Recieved!");
            c.latestEnrollment = null;
        }
    };

    // Initialize the widget
    c.initializeWidget = function() {
        c.fetchLatestEnrolledCourse();
    };

    // Call initialize function
    c.initializeWidget();
};

sp_widget.css:
