sp_widget.client_script:
api.controller = function($location, $scope) {
  /* widget controller */
  var c = this;

  // Function to redirect to course detail page
  c.redirectToCourseDetail = function(sys_id) {
    var portalURL = $location.absUrl().split('?')[0];
    var pageID = 'lms_course_details'; // Replace this with the actual page ID
    var url = portalURL + '?id=' + pageID + '&sys_id=' + sys_id;
    window.location.href = url;
  };

  // Function to show all courses (for initialization or clearing filters)
  c.showAllCourses = function() {
    c.filteredCourses = c.data.courses;
  };

  // Function to filter courses by domain based on domain selection from LMS Domain Slider
  c.filterCoursesByDomain = function(selectedDomain) {
    if (selectedDomain) {
      c.filteredCourses = c.data.courses.filter(function(course) {
        return course.Domain === selectedDomain;
      });
    } else {
      // No domain selected, show all courses
      c.showAllCourses();
    }
  };

  // Watch for changes in the URL parameters, especially the `domain` parameter
  $scope.$watch(function() {
    return $location.search();
  }, function(params) {
    var selectedDomain = params.domain;
    c.filterCoursesByDomain(selectedDomain);
  });

  // Initialize with all courses
  c.filteredCourses = c.data.courses;
};

