sp_widget.script:
(function () {
    var question_ref = input && input.parameters && input.parameters.sys_id;
    var question = input && input.parameters && input.parameters.question;
    var description = input && input.parameters && input.parameters.description;
    var question_by = input && input.parameters && input.parameters.question_by;

    if (question_ref) {
        data.question_ref = question_ref;
        data.question = question;
        data.description = description;
        data.question_by = question_by;

        // Fetch and populate answers from the "forum answers" table based on the selected question
        data.answers = []; // Array to hold answers
        var grAnswers = new GlideRecord('x_1383177_lms_app_forum_answers');
        grAnswers.addQuery('question_ref', question_ref);
        grAnswers.query();

        while (grAnswers.next()) {
            data.answers.push({
                author: grAnswers.getValue('answered_by'),
                content: grAnswers.getValue('answer')
            });
        }
    }
})();
