sp_widget.script:
(function() {
  if (!input) {
    data.course = null;
    var courseTable = 'x_1383177_lms_app_course';
    var fields = 'course_name,description,thumbnail';
    var courseSysId = $sp.getParameter('sys_id'); // Get sys_id from the query parameter

    if (courseSysId) {
      var gr = new GlideRecordSecure(courseTable);
      if (gr.get(courseSysId)) { // Get the specific course record
        data.course = $sp.getFieldsObject(gr, fields);
      }
    }
  }

  if (input) {
    // Handle user input if needed
  }
})();
