sp_widget.script:
(function() {
	var search = input ? input.search : '';  // Safely access 'input.search'
	var gr = new GlideRecord('x_1383177_lms_app_course');

	if (search) {
		gr.addQuery('course_name', 'CONTAINS', search);
	}

	gr.query();

	var courses = [];
	while (gr.next()) {
		courses.push({
			sys_id: gr.getUniqueValue(), // Ensure sys_id is included
			course_name: gr.getValue('course_name'),
			thumbnail: gr.getDisplayValue('thumbnail'),
			short_description: gr.getValue('short_description')
		});
	}

	data.courses = courses;
})();
