sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    //     var courseId = g_form.getValue('course_name');

    //     var ga = new GlideAjax('CourseAccessibilityUtil');
    //     ga.addParam('sysparm_name', 'getCourseAccessibility');
    //     ga.addParam('sysparm_course_id', courseId);

    //     ga.getXMLAnswer(function(response) {
    //         if (response == 'free') {
    //             g_form.setValue('status', 'approved');
    //         }
    //     });
    var courseId = newValue; // Assuming newValue is the sys_id of the selected course

    var ga = new GlideAjax('CourseAccessibilityUtil');
    ga.addParam('sysparm_name', 'getCourseAccessibility');
    ga.addParam('sysparm_course_id', courseId);

    ga.getXMLAnswer(function(response) {
        if (response == 'free') {
            // Update the status field using GlideRecord
            var enrollmentGr = new GlideRecord('x_1383177_lms_app_enrollment');
            if (enrollmentGr.get(control._gr.sys_id)) {
                enrollmentGr.setValue('status', 'approved');
                enrollmentGr.update();
            }
        }
    });
}
